import React, { Component } from 'react';
import NewSingle from './NewSingle';

class News extends Component {
    constructor(props){
        super(props);
        this.state = {
            news: [],
        };
    }

    componentDidMount(){
        const url='https://newsapi.org/v2/everything?q=tesla&from=2021-11-29&sortBy=publishedAt&apiKey=9a7f75898c5d4d319f49cf230b57ac39';

      fetch(url)
      .then((response) => {
        return response.json();
      })
      .then ((json) => console.log(json))
      .then((data) => {
        this.setState({
          news: data.articles
        })
      })
      .catch((error) => console.log(error));
  }

  renderItems() {
    return this.state.news.map((item) => (
      <NewSingle key={item.url} item={item} />
    ));
  }

  render() {
    return (
      <ul>
        {this.renderItems()}
      </ul>
    );
  }
}

export default News;
