    //     }
    //   }
    // })

    // axios.get(url)
    //   .then((response) => {
    //     this.setState({
    //       sidenews: response.data.articles
    //     })
    //   })
    //   .catch((error) => {
    //     this.setState({
    //       error: true
    //     })
    //   }); 

  }


  renderItems() {
    if(!this.state.error) {
      return this.state.sidenews.map((item) => (
        <SavedArticle key={item.url} item={item} />
      ));
    } else {
      return <Error />
    }
  }

  render() {
    return (
     <div className="row">
       {this.renderItems()}
     </div>   
    );
  }
}
export default SavedNews;
